mapper.select:

select 
	num, title, content, count, reg_date 
from
	(select 
	rownum rn, num, title, content, count, reg_date 
	from news 
	where rownum <= #{pageNum} * #{amount}
	)
where rn > (#{pageNum} - 1) * #{amount}

mapper.update:
update news set title=#{ndto.title},
content=#{ndto.content}
where num=${ndto.num}
